import { useEffect, useRef } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { X, Star, BadgeCheck, ShieldAlert } from "lucide-react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

const competitors = [
  {
    name: "Stockbit",
    subtitle: "Social",
    x: 30,
    y: 50,
    size: "md",
    bgColor: "bg-blue-500/20",
    borderColor: "border-blue-500/40",
  },
  {
    name: "XM Trading",
    subtitle: "Education",
    x: 70,
    y: 30,
    size: "md",
    bgColor: "bg-emerald-500/20",
    borderColor: "border-emerald-500/40",
  },
  {
    name: "Binomo",
    subtitle: "Gamified",
    x: 30,
    y: 70,
    size: "sm",
    bgColor: "bg-purple-500/15",
    borderColor: "border-purple-500/35",
  },
  {
    name: "Binance",
    subtitle: "Crypto",
    x: 70,
    y: 70,
    size: "md",
    bgColor: "bg-amber-500/20",
    borderColor: "border-amber-500/40",
  },
  {
    name: "Ajaib/Bibit",
    subtitle: "Robo-Advisor",
    x: 50,
    y: 85,
    size: "sm",
    bgColor: "bg-cyan-500/15",
    borderColor: "border-cyan-500/35",
  },
];

export default function EcosystemSection() {
  const sectionRef = useRef<HTMLDivElement>(null);
  const headerRef = useRef<HTMLDivElement>(null);
  const titleRef = useRef<HTMLHeadingElement>(null);
  const subtitleRef = useRef<HTMLParagraphElement>(null);
  const positioningTitleRef = useRef<HTMLHeadingElement>(null);
  const advantagesTitleRef = useRef<HTMLHeadingElement>(null);
  const mapRef = useRef<HTMLDivElement>(null);
  const tableRef = useRef<HTMLTableElement>(null);
  const bottomLineRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const section = sectionRef.current;
    const header = headerRef.current;
    const title = titleRef.current;
    const subtitle = subtitleRef.current;
    const positioningTitle = positioningTitleRef.current;
    const advantagesTitle = advantagesTitleRef.current;
    const map = mapRef.current;
    const table = tableRef.current;
    const bottomLine = bottomLineRef.current;

    if (!section || !map || !table) return;

    const ctx = gsap.context(() => {
      // Header animation (title and subtitle)
      if (title && subtitle) {
        gsap.set([title, subtitle], { opacity: 0, y: 50 });

        ScrollTrigger.create({
          trigger: header,
          start: "top 80%",
          onEnter: () => {
            const tl = gsap.timeline();
            tl.to(title, {
              opacity: 1,
              y: 0,
              duration: 0.8,
              ease: "power3.out",
            }).to(
              subtitle,
              {
                opacity: 1,
                y: 0,
                duration: 0.6,
                ease: "power3.out",
              },
              "-=0.4"
            );
          },
        });
      }

      // Sub-headings animation (Positioning Map and Competitive Advantages)
      if (positioningTitle) {
        gsap.set(positioningTitle, { opacity: 0, y: 30 });

        ScrollTrigger.create({
          trigger: positioningTitle,
          start: "top 85%",
          onEnter: () => {
            gsap.to(positioningTitle, {
              opacity: 1,
              y: 0,
              duration: 0.6,
              ease: "power3.out",
            });
          },
        });
      }

      if (advantagesTitle) {
        gsap.set(advantagesTitle, { opacity: 0, y: 30 });

        ScrollTrigger.create({
          trigger: advantagesTitle,
          start: "top 85%",
          onEnter: () => {
            gsap.to(advantagesTitle, {
              opacity: 1,
              y: 0,
              duration: 0.6,
              ease: "power3.out",
            });
          },
        });
      }

      // Bottom Line card animation
      if (bottomLine) {
        gsap.set(bottomLine, { opacity: 0, scale: 0.9, y: 40 });

        ScrollTrigger.create({
          trigger: bottomLine,
          start: "top 85%",
          onEnter: () => {
            gsap.to(bottomLine, {
              opacity: 1,
              scale: 1,
              y: 0,
              duration: 0.8,
              ease: "back.out(1.7)",
            });

            // Add pulsating glow effect after initial animation
            gsap.to(bottomLine, {
              boxShadow: "0 0 40px rgba(212, 175, 55, 0.6)",
              duration: 1.5,
              delay: 0.8,
              repeat: -1,
              yoyo: true,
              ease: "sine.inOut",
            });
          },
        });
      }

      const competitors = map.querySelectorAll("[data-competitor]");
      const tradex = map.querySelector("[data-tradex]");
      const starIcon = map.querySelector("[data-star-icon]");
      const horizontalAxis = map.querySelector('[data-axis="horizontal"]');
      const verticalAxis = map.querySelector('[data-axis="vertical"]');
      const gridLines = map.querySelectorAll("[data-grid-line]");

      gsap.set([competitors, tradex, horizontalAxis, verticalAxis, gridLines], {
        opacity: 0,
      });
      gsap.set(competitors, { scale: 0, y: 20 });
      gsap.set(tradex, { scale: 0 });
      gsap.set(horizontalAxis, { scaleX: 0, transformOrigin: "left" });
      gsap.set(verticalAxis, { scaleY: 0, transformOrigin: "top" });
      gsap.set(gridLines, { scaleY: 0 });
      if (starIcon) gsap.set(starIcon, { rotation: 0, scale: 1 });

      ScrollTrigger.create({
        trigger: map,
        start: "top 80%",
        onEnter: () => {
          const tl = gsap.timeline();

          tl.to(gridLines, {
            scaleY: 1,
            opacity: 0.4,
            duration: 0.8,
            stagger: 0.1,
            ease: "power2.out",
          })
            .to(
              horizontalAxis,
              {
                scaleX: 1,
                opacity: 0.8,
                duration: 0.8,
                ease: "power2.out",
              },
              "-=0.4",
            )
            .to(
              verticalAxis,
              {
                scaleY: 1,
                opacity: 0.8,
                duration: 0.8,
                ease: "power2.out",
              },
              "-=0.6",
            )
            .to(
              competitors,
              {
                scale: 1,
                y: 0,
                opacity: 1,
                duration: 0.6,
                stagger: 0.15,
                ease: "back.out(1.7)",
              },
              "-=0.4",
            )
            .to(
              tradex,
              {
                scale: 1,
                opacity: 1,
                duration: 0.8,
                ease: "back.out(2)",
              },
              "-=0.2",
            )
            .to(tradex, {
              boxShadow: "0 0 40px rgba(212, 175, 55, 0.6)",
              duration: 1.5,
              repeat: -1,
              yoyo: true,
              ease: "sine.inOut",
            });

          if (starIcon) {
            gsap.to(starIcon, {
              rotation: 360,
              duration: 8,
              repeat: -1,
              ease: "linear",
            });

            gsap.to(starIcon, {
              scale: 1.3,
              duration: 1.5,
              repeat: -1,
              yoyo: true,
              ease: "sine.inOut",
            });

            gsap.to(starIcon, {
              filter: "drop-shadow(0 0 10px rgba(212, 175, 55, 0.8))",
              duration: 1.2,
              repeat: -1,
              yoyo: true,
              ease: "sine.inOut",
            });
          }
        },
      });

      const tableRows = table.querySelectorAll("tbody tr");
      gsap.set(tableRows, { opacity: 0, x: -20 });

      ScrollTrigger.create({
        trigger: table,
        start: "top 85%",
        onEnter: () => {
          gsap.to(tableRows, {
            opacity: 1,
            x: 0,
            duration: 0.5,
            stagger: 0.1,
            ease: "power2.out",
          });
        },
      });
    }, section);

    return () => ctx.revert();
  }, []);

  return (
    <section ref={sectionRef} className="py-24 bg-card">
      <div className="container mx-auto px-6">
