import { useEffect, useRef, useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { TrendingUp, DollarSign, Target, ArrowUpRight, Sparkles, TrendingDown, X } from "lucide-react";
import month1Bg from "@assets/mount_1763611075459.png";
import month2Bg from "@assets/generated_images/Month_2_profitability_dashboard_b88a69c2.png";
import month3Bg from "@assets/generated_images/Month_3_growth_analytics_1c72396a.png";
import chartBg from "@assets/generated_images/Revenue_growth_chart_visualization_92f45469.png";
import metricsBg from "@assets/generated_images/Unit_economics_metrics_dashboard_38204899.png";

const bepPhases = [
  {
    month: "Month 1 - 7",
    background: month1Bg,
    borderColor: "border-red-500/50",
    glowColor: "shadow-red-500/20",
    isNegative: true,
    metrics: {
      users: 550,
      paying: 300,
      revenue: "Rp 192M",
      expenses: "Rp 250M",
      net: "- Rp 58M",
      margin: null,
    },
  },
  {
    month: "Month 8 - 9",
    background: month2Bg,
    borderColor: "border-blue-400/50",
    glowColor: "shadow-blue-400/20",
    isNegative: false,
    metrics: {
      users: 800,
      paying: 425,
      revenue: "Rp 272M",
      expenses: null,
      net: null,
      breakeven: "Rp 272M",
      margin: "~12%",
    },
  },
  {
    month: "Month 10 - 11",
    background: month2Bg,
    borderColor: "border-blue-500/50",
    glowColor: "shadow-blue-500/20",
    isNegative: false,
    metrics: {
      users: 1000,
      paying: 550,
      revenue: "Rp 352M",
      expenses: null,
      net: null,
      breakeven: "Rp 352M",
      margin: "~18%",
    },
  },
  {
    month: "Month 12 - 13",
    background: month2Bg,
    borderColor: "border-indigo-500/50",
    glowColor: "shadow-indigo-500/20",
    isNegative: false,
    metrics: {
      users: 1250,
      paying: 675,
      revenue: "Rp 432M",
      expenses: null,
      net: null,
      breakeven: "Rp 432M",
      margin: "~23%",
    },
  },
  {
    month: "Month 14 - 15",
    background: month2Bg,
    borderColor: "border-purple-500/50",
    glowColor: "shadow-purple-500/20",
    isNegative: false,
    metrics: {
      users: 1550,
      paying: 800,
      revenue: "Rp 512M",
      expenses: null,
      net: null,
      breakeven: "Rp 512M",
      margin: "~28%",
    },
  },
  {
    month: "Month 16 - 23",
    background: month3Bg,
    borderColor: "border-green-500/50",
    glowColor: "shadow-green-500/20",
    isNegative: false,
    metrics: {
      users: 2800,
      paying: 1800,
      revenue: "Rp 1.15B",
      expenses: null,
      net: null,
      breakeven: "Rp 820M",
      margin: "~29%",
    },
  },
];

const chartData = [
  { month: "M1-7", revenue: 30, net: "-58M", isNegative: true },
  { month: "M8-9", revenue: 42, net: "+33M", isNegative: false },
  { month: "M10-11", revenue: 55, net: "+63M", isNegative: false },
  { month: "M12-13", revenue: 68, net: "+99M", isNegative: false },
  { month: "M14-15", revenue: 80, net: "+143M", isNegative: false },
  { month: "M16-23", revenue: 100, net: "+330M", isNegative: false },
];

const keyMetricsData = [
  { label: "Gross margin", value: "73%", animated: 73 },
  { label: "Revenue/User", value: "Rp 250K", animated: 250 },
  { label: "Fixed Costs", value: "Rp 465M", animated: 465 },
  { label: "CAC", value: "Rp 750K", animated: 750, highlight: true },
];

const unitEconomicsData = [
  { label: "CAC", value: "Rp 750K", animated: 750 },
  { label: "LTV", value: "Rp 7.68M", animated: 7.68 },
  { label: "LTV/CAC", value: "10.24x", animated: 10.24 },
  { label: "Payback", value: "3 months", animated: 3 },
  { label: "Gross Margin", value: "73%", animated: 73, highlight: true },
];

export default function BreakEvenSection() {
  const sectionRef = useRef<HTMLElement>(null);
  const cardsRef = useRef<HTMLDivElement>(null);
  const chartRef = useRef<HTMLDivElement>(null);
  const metricsRef = useRef<HTMLDivElement>(null);
  const [chartHeights, setChartHeights] = useState(chartData.map(() => 0));
  const [userCounts, setUserCounts] = useState(bepPhases.map(() => 0));
  const [payingCounts, setPayingCounts] = useState(bepPhases.map(() => 0));
  const [keyMetricsValues, setKeyMetricsValues] = useState(keyMetricsData.map(() => 0));
  const [economicsValues, setEconomicsValues] = useState(unitEconomicsData.map(() => 0));
  const hasAnimatedRef = useRef(false);
  const isMountedRef = useRef(true);

  const formatNumber = (num: number) => {
    return num.toLocaleString("id-ID");
  };

  useEffect(() => {
    if (typeof window === "undefined") return;

    isMountedRef.current = true;
    const section = sectionRef.current;
    const cards = cardsRef.current;
    const chart = chartRef.current;
    const metrics = metricsRef.current;

    if (!section || !cards || !chart || !metrics) return;

    let gsap: typeof import("gsap").default;
    let ScrollTrigger: typeof import("gsap/ScrollTrigger").ScrollTrigger;

    const initAnimation = async () => {
      const gsapModule = await import("gsap");
      const scrollTriggerModule = await import("gsap/ScrollTrigger");
      gsap = gsapModule.default;
      ScrollTrigger = scrollTriggerModule.ScrollTrigger;
      gsap.registerPlugin(ScrollTrigger);

      const prefersReducedMotion = window.matchMedia("(prefers-reduced-motion: reduce)").matches;

      if (prefersReducedMotion) {
        const bepCards = cards.querySelectorAll("[data-bep-card]");
        const lossBars = chart.querySelectorAll('[data-bar-type="loss"]');
        const profitBars = chart.querySelectorAll('[data-bar-type="profit"]');
        const metricCards = metrics.querySelectorAll("[data-metric-card]");

        gsap.set([bepCards, lossBars, profitBars, metricCards], { opacity: 1, y: 0, scale: 1 });

        if (isMountedRef.current) {
          setChartHeights(chartData.map((d) => d.revenue));
          setUserCounts(bepPhases.map((p) => p.metrics.users));
          setPayingCounts(bepPhases.map((p) => p.metrics.paying));
          setKeyMetricsValues(keyMetricsData.map((m) => m.animated));
          setEconomicsValues(unitEconomicsData.map((m) => m.animated));
        }
        return;
      }

      const ctx = gsap.context(() => {
        const bepCards = cards.querySelectorAll("[data-bep-card]");
        const metricCards = metrics.querySelectorAll("[data-metric-card]");

        gsap.set([bepCards, metricCards], { opacity: 0, y: 30 });

        const tl = gsap.timeline({
          scrollTrigger: {
            trigger: section,
            start: "top 70%",
            toggleActions: "play none none none",
            once: true,
          },
        });

        // Animate BEP cards with stagger
        tl.to(bepCards, {
          opacity: 1,
          y: 0,
          duration: 0.8,
          stagger: 0.15,
          ease: "power3.out",
          onComplete: () => {
            if (!hasAnimatedRef.current && isMountedRef.current) {
              hasAnimatedRef.current = true;

              // Animate user and paying counts
              bepPhases.forEach((phase, index) => {
                gsap.to(
                  {},
                  {
                    duration: 1.5,
                    ease: "power2.out",
                    onUpdate: function () {
                      if (isMountedRef.current) {
                        const progress = this.progress();
                        setUserCounts((prev) => {
                          const newCounts = [...prev];
                          newCounts[index] = Math.floor(phase.metrics.users * progress);
                          return newCounts;
                        });
                        setPayingCounts((prev) => {
                          const newCounts = [...prev];
                          newCounts[index] = Math.floor(phase.metrics.paying * progress);
                          return newCounts;
                        });
                      }
                    },
                  }
                );
              });
            }
          },
        });

        // Separate loss and profit bars for conditional animations
        const lossBars = chart.querySelectorAll('[data-bar-type="loss"]');
        const profitBars = chart.querySelectorAll('[data-bar-type="profit"]');
        const zeroLine = chart.querySelector('[data-testid="axis-zero-line"]');

        // Set initial states for conditional animations
        gsap.set(lossBars, { opacity: 0, y: -80, scale: 0.8, rotationX: -15 });
        gsap.set(profitBars, { opacity: 0, y: 60, scale: 0.7, rotationX: 20 });
        gsap.set(zeroLine, { scaleX: 0, opacity: 0 });

        // Animate zero line first
        tl.to(
          zeroLine,
          {
            scaleX: 1,
            opacity: 1,
            duration: 0.6,
            ease: "power2.out",
          },
          "-=0.2"
        );

        // Animate loss bars - falling from above with shake effect
        tl.to(
          lossBars,
          {
            opacity: 1,
            y: 0,
            scale: 1,
            rotationX: 0,
            duration: 0.8,
            stagger: 0.1,
            ease: "bounce.out",
            onStart: () => {
              // Animate the first bar (loss bar) height
              chartData.forEach((data, index) => {
                if (data.isNegative) {
                  gsap.to(
                    {},
                    {
                      duration: 1.2,
                      delay: index * 0.1,
                      ease: "elastic.out(1, 0.5)",
                      onUpdate: function () {
                        if (isMountedRef.current) {
                          const progress = this.progress();
                          setChartHeights((prev) => {
                            const newHeights = [...prev];
                            newHeights[index] = data.revenue * progress;
                            return newHeights;
                          });
                        }
                      },
                    }
                  );
                }
              });
            },
            onComplete: () => {
              // Add shake effect for loss bars
              gsap.to(lossBars, {
                x: 3,
                duration: 0.1,
                repeat: 5,
                yoyo: true,
                ease: "power1.inOut",
                onComplete: () => {
                  gsap.set(lossBars, { x: 0 });
                }
              });
              // Add red glow pulse for loss
              gsap.to(lossBars, {
                filter: "drop-shadow(0 0 12px rgba(239, 68, 68, 0.8))",
                duration: 0.8,
                repeat: 2,
                yoyo: true,
                ease: "sine.inOut",
              });
            },
          },
          "-=0.3"
        );

        // Animate profit bars - growing from below with bounce effect
        tl.to(
          profitBars,
          {
            opacity: 1,
            y: 0,
            scale: 1,
            rotationX: 0,
            duration: 0.7,
            stagger: 0.12,
            ease: "back.out(1.7)",
            onStart: () => {
              // Animate profit bar heights with growth effect
              chartData.forEach((data, index) => {
                if (!data.isNegative) {
                  gsap.to(
                    {},
                    {
                      duration: 1,
                      delay: (index - 1) * 0.12,
                      ease: "power3.out",
                      onUpdate: function () {
                        if (isMountedRef.current) {
                          const progress = this.progress();
                          setChartHeights((prev) => {
                            const newHeights = [...prev];
                            newHeights[index] = data.revenue * progress;
                            return newHeights;
                          });
                        }
                      },
                    }
                  );
                }
              });
            },
            onComplete: () => {
              // Add green glow celebration for profit bars
              gsap.to(profitBars, {
                filter: "drop-shadow(0 0 15px rgba(16, 185, 129, 0.9))",
                duration: 0.6,
                stagger: 0.1,
                repeat: 1,
                yoyo: true,
                ease: "sine.inOut",
              });
              // Subtle scale pulse for success
              gsap.to(profitBars, {
                scale: 1.03,
                duration: 0.4,
                stagger: 0.08,
                repeat: 1,
                yoyo: true,
                ease: "power1.inOut",
              });
            },
          },
          "-=0.4"
        );

        // Animate metric cards
        tl.to(
          metricCards,
          {
            opacity: 1,
            y: 0,
            duration: 0.6,
            stagger: 0.15,
            ease: "power3.out",
            onComplete: () => {
              // Animate key metrics values
              keyMetricsData.forEach((metric, index) => {
                gsap.to(
                  {},
                  {
                    duration: 1.2,
                    ease: "power2.out",
                    onUpdate: function () {
                      if (isMountedRef.current) {
                        const progress = this.progress();
                        setKeyMetricsValues((prev) => {
                          const newValues = [...prev];
                          newValues[index] = metric.animated * progress;
                          return newValues;
                        });
                      }
                    },
                  }
                );
              });

              // Animate economics values
              unitEconomicsData.forEach((metric, index) => {
                gsap.to(
                  {},
                  {
                    duration: 1.2,
                    delay: 0.2,
                    ease: "power2.out",
                    onUpdate: function () {
                      if (isMountedRef.current) {
                        const progress = this.progress();
                        setEconomicsValues((prev) => {
                          const newValues = [...prev];
                          newValues[index] = metric.animated * progress;
                          return newValues;
                        });
                      }
                    },
                  }
                );
              });
            },
          },
          "-=0.4"
        );

        // Add floating animation to cards
        gsap.to(bepCards, {
          y: -5,
          duration: 2,
          ease: "power1.inOut",
          stagger: 0.2,
          repeat: -1,
          yoyo: true,
          delay: 1,
        });

        // Add pulse effect to highlight metrics
        const highlights = section.querySelectorAll("[data-highlight]");
        gsap.to(highlights, {
          scale: 1.02,
          duration: 1.5,
          ease: "power1.inOut",
          repeat: -1,
          yoyo: true,
          delay: 1.5,
        });
      }, section);

      return ctx;
    };

    const cleanup = initAnimation();

    return () => {
      isMountedRef.current = false;
      cleanup.then((ctx) => ctx?.revert());
    };
  }, []);

  return (
    <section
      ref={sectionRef}
      className="py-24 bg-gradient-to-b from-card to-black relative overflow-hidden"
    >
      {/* Background effects */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-primary/5 rounded-full blur-3xl" />
        <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-blue-500/5 rounded-full blur-3xl" />
      </div>

      <div className="container mx-auto px-6 relative z-10">
        <div className="text-center mb-16">
          <div className="flex items-center justify-center gap-2 mb-4">
            <Sparkles className="text-primary w-6 h-6 animate-pulse" />
            <h2
              className="text-4xl md:text-5xl font-bold text-gold-gradient"
              data-testid="text-breakeven-title"
            >
              Break-Even in Month 8-15
            </h2>
            <Sparkles className="text-primary w-6 h-6 animate-pulse" />
          </div>
          <p
            className="text-muted-foreground text-lg max-w-3xl mx-auto mb-4"
            data-testid="text-breakeven-subtitle"
          >
            Rapid Path to Profitability
          </p>
          <div className="flex items-center justify-center gap-2">
            <Badge
              variant="outline"
              className="border-primary/50 px-4 py-1.5 text-[19px] bg-[#d6b13ad1] text-[#0d0901] shadow-lg shadow-primary/20"
            >
              Sustainable Growth Model
            </Badge>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-12 gap-6 mb-12">
          {/* Left Column - BEP Phases */}
          <div ref={cardsRef} className="lg:col-span-5 grid grid-cols-1 sm:grid-cols-2 gap-3">
            {bepPhases.map((phase, index) => (
              <Card
                key={index}
                data-bep-card="true"
                className={`relative overflow-hidden ${phase.borderColor} border-2 hover-elevate transition-all duration-300 shadow-lg ${phase.glowColor} hover:shadow-xl`}
                data-testid={`card-bep-${index}`}
              >
                <img
                  src={phase.background}
                  alt={`${phase.month} break-even metrics`}
                  className="absolute inset-0 w-full h-full object-cover"
                  data-testid={`img-bep-bg-${index}`}
                />
                <div className="absolute inset-0 bg-gradient-to-br from-black/90 via-black/85 to-black/90" />

                <CardContent className="relative p-3">
                  <div className="flex items-center justify-between mb-2">
                    <Badge
                      className="bg-gradient-to-r from-[#D4AF37] to-[#F7E27A] text-black font-bold px-2 py-0.5 text-xs shadow-md"
                      data-testid={`badge-month-${index}`}
                    >
                      {phase.month}
                    </Badge>
                    <div className="flex items-center gap-1">
                      {phase.isNegative ? (
                        <>
                          <TrendingDown className="text-red-500 animate-pulse" size={16} />
                          <X className="text-red-400" size={12} />
                        </>
                      ) : (
                        <>
                          <TrendingUp className="text-gold-gradient animate-pulse" size={16} />
                          <ArrowUpRight className="text-green-400" size={12} />
                        </>
                      )}
                    </div>
                  </div>

                  <div className="space-y-1.5">
                    <div className="flex justify-between items-center text-xs bg-white/5 backdrop-blur-sm rounded-md px-2 py-1.5 border border-white/10 hover:bg-white/10 transition-colors">
                      <span className="text-white/90">Users:</span>
                      <span className={phase.isNegative ? "text-white font-bold" : "text-gold-gradient font-bold"}>
                        {formatNumber(userCounts[index])}
                      </span>
                    </div>
                    <div className="flex justify-between items-center text-xs bg-white/5 backdrop-blur-sm rounded-md px-2 py-1.5 border border-white/10 hover:bg-white/10 transition-colors">
                      <span className="text-white/90">Paying:</span>
                      <span className={phase.isNegative ? "text-white font-bold" : "text-gold-gradient font-bold"}>
                        {formatNumber(payingCounts[index])}
                      </span>
                    </div>
                    <div className="flex justify-between items-center text-xs bg-white/5 backdrop-blur-sm rounded-md px-2 py-1.5 border border-white/10 hover:bg-white/10 transition-colors">
                      <span className="text-white/90">Revenue:</span>
                      <span className={phase.isNegative ? "text-white font-bold" : "text-gold-gradient font-bold"}>
                        {phase.metrics.revenue}
                      </span>
                    </div>
                    {phase.metrics.expenses && (
                      <div className="flex justify-between items-center text-xs bg-white/5 backdrop-blur-sm rounded-md px-2 py-1.5 border border-white/10 hover:bg-white/10 transition-colors">
                        <span className="text-white/90">Expenses:</span>
                        <span className="text-white font-bold">
                          {phase.metrics.expenses}
                        </span>
                      </div>
                    )}
                    {phase.metrics.breakeven && (
                      <div className="flex justify-between items-center text-xs bg-white/5 backdrop-blur-sm rounded-md px-2 py-1.5 border border-white/10 hover:bg-white/10 transition-colors">
                        <span className="text-white/90">Break-even:</span>
                        <span className="text-gold-gradient font-bold">
                          {phase.metrics.breakeven}
                        </span>
                      </div>
                    )}
                    {phase.metrics.net && (
                      <div 
                        className="flex justify-between items-center text-xs bg-gradient-to-r from-red-500/20 to-red-500/10 backdrop-blur-sm rounded-md px-2 py-1.5 border border-red-500/30 shadow-md"
                        data-highlight="true"
                      >
                        <span className="text-white font-medium">Net:</span>
                        <span className="text-red-400 font-bold text-sm flex items-center gap-1">
                          {phase.metrics.net}
                          <X size={12} />
                        </span>
                      </div>
                    )}
                    {phase.metrics.margin && (
                      <div 
